INSERT INTO `cara_pendaftaran` (`id`, `step`, `nama_step`, `deskripsi`, `logo`, `created_at`, `updated_at`) VALUES (1,'Step 1','Transfer Pembayaran Pendaftaran','<p>Sebelum mengirim/ Submit pendaftaran, Abu/Ummu terlebih dahulu melakukan transfer uang pendaftaran sejumlah Rp. 200.000,- dan di transfer ke :</p>\r\n<div>Nama Bank :</div>\r\n<div><strong>Bank Syariah Indonesia (BSI)</strong></div>\r\n<div>Nama Rekening :</div>\r\n<div><strong>EDRITA</strong></div>\r\n<div>Nomor Rekening:</div>\r\n<div><strong>7140195377</strong></div>\r\n<p>Catatan: Pastikan Abu/Ummu menyimpan atau&nbsp;<em>screen capture&nbsp;</em>bukti transfer sebagai syarat pendaftaran santri baru PONPES ABA</p>\r\n<p>&nbsp;</p>','flaticon-user-1','2024-01-04 22:34:57','2024-01-31 20:16:02'),(2,'Step 2','Persiapan Berkas','<p>Sebelum mengisi formulir di halaman pendaftaran, ada baiknya Abu/Ummu telah menyiapkan beberapa file/softcopy yang akan di upload nantinya. Yang Abu/Ummu perlu siapkan adalah :</p>\n<p style=\"text-align: justify;\">&nbsp;1. Softcopy Bukti Transfer Pendaftaran<br />&nbsp;2. Softcopy Kartu Keluarga (KK)<br />&nbsp;3. Softcopy Pas Photo Bewarna<br />&nbsp;4. Softcopy Akta Kelahiran Santri<br />&nbsp;5. Surat Keterangan duduk kelas 6 SD (Wustho)<br />&nbsp;6. Surat Keterangan duduk kelas 9 SMP (Ulya &amp; I\'dad ulya)<br />&nbsp;7. Usia Minimal 15 Tahun (Takhosus Al-Qur\'an)<br />&nbsp;8. Sofcopy Raport kelas 5 (Wustho) / kelas 8 (Ulya)<br />&nbsp;9. Softcopy KTP Kedua Orang Tua/ Wali</p>','flaticon-reading','2024-01-04 23:39:56','2024-02-02 02:03:03'),(3,'Step 3','Mengisi Formulir','<p>Setelah melakukan transfer uang pendaftaran. Abu/ummu dapat mengisi formulir pendaftaran santri yang tersedia di :&nbsp;</p>\r\n<p><span style=\"color: #e67e23;\"><a href=\"../../ppdb\">PONPES ABA [Klik Daftar Sekarang]</a></span></p>\r\n<p>Saat mengisi fomulir, jangan lupa untuk meng-upload bukti transfer pembayaran pada form yang tersedia. Pastikan mengisi data dengan benar.</p>','flaticon-upload','2024-01-05 01:30:15','2024-02-02 01:50:11'),(4,'Step 4','Mengikuti Rangkaian Tes Masuk PONPES ABA','<p style=\"text-align: justify;\">Setelah Abu/Umma melakukan transfer uang pendaftaran dan mengisi formulir, silahkan tunggu info selanjutnya dari PONPES ABA untuk tanggal dan rangkaian Tes selanjutnya.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Adapun rangkaian tes penerimaan santri PONPES ABA adalah :</p>\r\n<p>- Tes Wawancara Orang tua dan Anak</p>\r\n<p>- Tes kemampuan Menghafal Anak</p>\r\n<p>- Tes membaca Al-Qur\'an Orang tua dan anak</p>\r\n<p style=\"text-align: justify;\">Untuk detail ujian dan waktu akan diinfokan selanjutnya bersama pengumuman dari PONPES ABA</p>','flaticon-round-table','2024-01-06 00:43:23','2024-02-02 01:50:23'),(5,'Step 5','Pengumuman dan Registrasi Ulang','<p>Info registrasi ulang akan diumumkan bersamaan dengan info kelulusan santri PONPES ABA T.A 2024/2025</p>','flaticon-clock-2','2024-01-06 00:45:33','2024-01-06 00:45:33');
