INSERT INTO `ket_class` (`id`, `id_program`, `judul_1`, `deskripsi_judul_1`, `judul_2`, `deskripsi_judul_2`, `created_at`, `updated_at`, `image`) VALUES (4,1,'Metode Pembelajaran','<p style=\"text-align: justify;\">Metode pembelajaran yang digunakan dalam program ini dapat mencakup metode ceramah, diskusi kelompok, tanya jawab, presentasi, dan pemberian tugas.</p>\r\n<p style=\"text-align: justify;\">Peserta didik akan diajak untuk aktif berpartisipasi dalam proses pembelajaran dan diberikan kesempatan untuk berinteraksi dengan guru dan sesama peserta didik.&nbsp;Selain itu, metode pembelajaran yang digunakan juga dapat disesuaikan dengan karakteristik peserta didik dan materi pelajaran yang diajarkan.</p>','Informasi Lainnya','<p class=\"mb-25\" style=\"text-align: justify;\">Kurikulum yang digunakan di Pondok Pesantren Abdurrahman Bin Auf adalah kombinasi antara kurikulum khas pondok pesantren dan kurikulum pemerintah</p>\r\n<div class=\"bd-program-details-list\">\r\n<ul>\r\n<li>Kurikulum pondok pesantren mencakup beberapa mata pelajaran yang menjadi ciri khas pondok pesantren, seperti tahfizh dan tahsin, Bahasa Arab, Siroh, Akidah, Akhlak, zikir dan doa, hadist, fiqih, mahfudzot, dan nahwu.</li>\r\n<li>Sedangkan kurikulum pemerintah mencakup beberapa mata pelajaran yang menjadi syarat untuk santri dapat mengikuti Ujian Negeri (program salafiyah) atau Ujian Nasional paket kesetaraan untuk tingkat yang sesuai.</li>\r\n</ul>\r\n</div>',NULL,'2024-02-01 02:33:21','202402010933Muhadarah.jpg'),(5,2,'Metode Pembelajaran','<p style=\"text-align: justify;\">Metode pembelajaran yang digunakan dalam program ini juga dapat mencakup metode ceramah, diskusi kelompok, tanya jawab, presentasi, dan pemberian tugas.</p>\r\n<p style=\"text-align: justify;\">Peserta didik akan diajak untuk aktif berpartisipasi dalam proses pembelajaran dan diberikan kesempatan untuk berinteraksi dengan guru dan sesama peserta didik. Metode pembelajaran yang digunakan juga dapat disesuaikan dengan karakteristik peserta didik dan materi pelajaran yang diajarkan.</p>','Informasi Lainnya','<p class=\"mb-25\" style=\"text-align: justify;\">Kurikulum yang digunakan di Pondok Pesantren Abdurrahman Bin Auf adalah kombinasi antara kurikulum khas pondok pesantren dan kurikulum pemerintah</p>\r\n<div class=\"bd-program-details-list\">\r\n<ul>\r\n<li>Kurikulum pondok pesantren mencakup beberapa mata pelajaran yang menjadi ciri khas pondok pesantren, seperti tahfizh dan tahsin, Bahasa Arab, Siroh, Akidah, Akhlak, zikir dan doa, hadist, fiqih, mahfudzot, dan nahwu.</li>\r\n<li>Sedangkan kurikulum pemerintah mencakup beberapa mata pelajaran yang menjadi syarat untuk santri dapat mengikuti Ujian Negeri (program salafiyah) atau Ujian Nasional paket kesetaraan untuk tingkat yang sesuai.</li>\r\n</ul>\r\n</div>',NULL,'2024-02-01 02:35:01','202402010935Muhadarah.jpg'),(6,3,'Metode Pembelajaran','<p style=\"text-align: justify;\">Metode pembelajaran yang digunakan dalam program ini akan fokus pada pembelajaran dan penghafalan Al-Qur\'an.&nbsp;Peserta didik akan mendapatkan pembelajaran intensif mengenai tajwid (ilmu bacaan Al-Qur\'an), memahami makna ayat-ayat Al-Qur\'an, dan menghafal Al-Qur\'an.</p>\r\n<p style=\"text-align: justify;\">Metode pembelajaran yang digunakan dalam program ini dapat mencakup metode repetisi, pengulangan, dan penggunaan teknik menghafal Al-Qur\'an.</p>','Informasi Lainnya','<p class=\"mb-25\" style=\"text-align: justify;\">Kurikulum yang digunakan untuk Takhosus Al-Qur\'an di Pondok Pesantren Abdurrahman Bin Auf mencakup:</p>\r\n<div class=\"bd-program-details-list\">\r\n<ul>\r\n<li>Tajwid: Ilmu bacaan Al-Qur\'an yang meliputi aturan-aturan dalam membaca huruf-huruf Al-Qur\'an dengan benar.</li>\r\n<li>Memahami makna ayat-ayat Al-Qur\'an: Peserta didik akan belajar untuk memahami makna dan tafsir dari ayat-ayat Al-Qur\'an.</li>\r\n<li>Menghafal Al-Qur\'an: Peserta didik akan diajarkan teknik dan metode dalam menghafal Al-Qur\'an.</li>\r\n<li>Pelajaran-pelajaran terkait Al-Qur\'an: Seperti tafsir, hadis, dan sejarah Nabi Muhammad SAW.</li>\r\n</ul>\r\n</div>',NULL,'2024-02-01 02:43:34','202402010937Muhadarah.jpg');
