INSERT INTO `visi_misi` (`id`, `visi`, `misi`, `motto`, `created_at`, `updated_at`) VALUES (1,'<p style=\"text-align: justify;\">Membentuk Generasi Yang Hafal Kitabullah, Unggul Dalam Ilmu Agama Dan Bahasa Arab, Sehingga Bermanfaat Bagi Diri dan Masyarakat.</p>','<ul>\r\n<li style=\"text-align: justify;\">Mewujudkan suasana islami dilingkungan pesantren.</li>\r\n<li style=\"text-align: justify;\">Menumbuhkan kecintaan terhadap Al-Qur\'an dan sunnah Nabi Shallallahu Alaihi Wasallam.</li>\r\n<li style=\"text-align: justify;\">Mendidik santri dan santriwati sehingga memiliki Tahfidz dan Tahsin yang baik.</li>\r\n<li style=\"text-align: justify;\">Meningkatkan pemahaman dan pengalaman agama islam secara baik,m eliputi Aqidah, ibadah dan juga muamalah</li>\r\n<li style=\"text-align: justify;\">Menciptakan suasana dan lingkungan pesantren yang berbahasa Arab</li>\r\n<li style=\"text-align: justify;\">Menciptakan santri dan santriwati yang berakhlakul karimah</li>\r\n<li style=\"text-align: justify;\">Melaksanakan pembelajaran aktif, kreatif, efektif dan menyenangkan untuk mengembangkan potensi peserta didik</li>\r\n<li style=\"text-align: justify;\">meningkatkan mutu sumber daya, sarana prasaranan yang menunjang.</li>\r\n<li style=\"text-align: justify;\">Menjadikan santri/wati lulusan mampu melanjutkan pendidikan yang lebih tinggi dalam maupun luar negri.</li>\r\n</ul>','Mencetak Generasi Rabbani, Berakhlak Qur\'ani, dan Cinta NKRI.',NULL,'2024-02-07 20:13:47');
